mapper.select:
select scode, s.pcode pcode, psize, pcolor, pprice, pamount, deliverfee_yn
from product p,stock s
where p.pcode = s.pcode
and p.pcode = #{PCODE}
and pcolor = #{PCOLOR}
and psize = #{PSIZE}
and scode = #{SCODE}
order by pcolor,psize

select p.pcode pcode,   styletop, stylemid, stylebot, pname, pimage, pregitdate, m.pPrice min_price , nvl(searchedCnt,0) popularity
from product p, stock s , (select pCode,min(pPrice) pPrice
						   				from stock
						   				group by pCode) m, searchedWord w
where p.pcode = s.pcode and s.pcode = m.pcode and p.pname = w.sword(+)
	and styleMid in
	 	
	 		#{item}	
	 	
	 
	and styleBot in
	 	
	 		#{item}	
	 	
	 
	and 
		
	 		pName like '%'||#{item}||'%'	
	 	
	
	and pSize 
		
			 =
	 			
	 				#{item}	
	 			
	 		
		 	 between
	 			
	 				#{item}	
	 			
	 		
	 	
	
	and pColor in 
		 
	 		#{item}	
	 	
	
	and m.pPrice between #{pPrice_min} and #{pPrice_max}
	and deliverFee_YN = #{deliverFee_YN}
	and p.pcode = #{PCODE}
order by to_number(p.pcode)

mapper.insert:
merge into stock s using
select scode, pamount-#{item.PAMOUNT} res from stock where scode = #{item.SCODE}
e on (s.scode = e.scode) when matched then update set s.pamount = e.res
